import React from 'react';

const ProductCard = ({ product, addToCart }) => {
  const { name, listPrice, price, image } = product;
  const hasDiscount = listPrice > price;

  return (
    <div className="product-card">
      <img src={image} alt={name} />
      <h3>{name}</h3>
      {hasDiscount && <span className="discount-flag">Desconto</span>}
      <p className="price-old">De: R${listPrice.toFixed(2)}</p>
      <p className="price-new">Por: R${price.toFixed(2)}</p>
      <button onClick={() => addToCart(product)}>Adicionar ao Carrinho</button>
    </div>
  );
};

export default ProductCard;
