import React from 'react';

const ProductCard = ({ name, oldPrice, price, image }) => {
  const hasDiscount = oldPrice > price;

  return (
    <div className="product-card">
      <img src={image} alt={name} />
      <h3>{name}</h3>
      {hasDiscount && <span className="discount-flag">Desconto</span>}
      <p className="price-old">De: R${oldPrice}</p>
      <p className="price-new">Por: R${price}</p>
    </div>
  );
};

export default ProductCard;
