import React, { useState, useEffect } from 'react';
import { Container, Row, Col, Button } from 'react-bootstrap';
import ProductCard from './components/ProductCard';
import 'bootstrap/dist/css/bootstrap.min.css';

const App = () => {
  const [products, setProducts] = useState([]);
  const [cart, setCart] = useState([]);
  const [showCart, setShowCart] = useState(false);

  useEffect(() => {
    const data = {
      "products": [
        {
          "name": "CalÃ§a Alfaiataria Rendada",
          "listPrice": 243.90,
          "price": 225.90,
          "image": "https://lojalancaperfume.vtexassets.com/arquivos/ids/504997-150-200/"
        },
        {
          "name": "CalÃ§a Alfaiataria Rendada",
          "listPrice": 243.90,
          "price": 225.90,
          "image": "https://lojalancaperfume.vtexassets.com/arquivos/ids/504997-150-200/"
        },
        {
          "name": "CalÃ§a Alfaiataria Rendada",
          "listPrice": 243.90,
          "price": 225.90,
          "image": "https://lojalancaperfume.vtexassets.com/arquivos/ids/504997-150-200/"
        }
      ]
    };
    setProducts(data.products);
  }, []);

  const addToCart = (product) => {
    setCart([...cart, product]);
  };

  const clearCart = () => {
    setCart([]);
  };

  return (
    <Container>
      <header className="d-flex justify-content-between align-items-center py-3">
        <h1>Vitrine de Produtos</h1>
        <div className="d-flex align-items-center">
          <div className="cart position-relative" onClick={() => setShowCart(!showCart)}>
            <span>ðŸ›’</span>
            <span className="badge bg-danger">{cart.length}</span>
          </div>
          <Col className="ml-3">
            <Button variant="danger" onClick={clearCart}>
              Limpar Carrinho
            </Button>
          </Col>
        </div>
      </header>

      {showCart && (
        <div className="cart-items">
          <h2>Itens no Carrinho</h2>
          <ul>
            {cart.map((item, index) => (
              <li key={index}>{item.name} - R${item.price.toFixed(2)}</li>
            ))}
          </ul>
        </div>
      )}

      <Row>
        {products.map((product, index) => (
          <Col key={index} md={4} className="mb-4">
            <ProductCard product={product} addToCart={addToCart} />
          </Col>
        ))}
      </Row>
    </Container>
  );
}

export default App;
