import React, { useState, useEffect } from 'react';
import { Container, Row, Col, Button, Card } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';

const App = () => {
  const [products, setProducts] = useState([]);
  const [cart, setCart] = useState([]);
  const [showCart, setShowCart] = useState(false);

  useEffect(() => {
    const data = {
      "products": [
        {
          "name": "CalÃ§a Alfaiataria Rendada",
          "listPrice": 243.90,
          "price": 225.90,
          "image": "https://lojalancaperfume.vtexassets.com/arquivos/ids/504997-150-200/"
        },
        {
          "name": "CalÃ§a Alfaiataria Rendada",
          "listPrice": 243.90,
          "price": 225.90,
          "image": "https://lojalancaperfume.vtexassets.com/arquivos/ids/504997-150-200/"
        },
        {
          "name": "CalÃ§a Alfaiataria Rendada",
          "listPrice": 243.90,
          "price": 225.90,
          "image": "https://lojalancaperfume.vtexassets.com/arquivos/ids/504997-150-200/"
        }
      ]
    };
    setProducts(data.products);
  }, []);

  const addToCart = (product) => {
    setCart([...cart, product]);
  };

  const clearCart = () => {
    setCart([]);
  };

  return (
    <Container>
      <header className="d-flex justify-content-between align-items-center py-3 position-relative">
        <h1>Vitrine de Produtos</h1>
        <div className="d-flex align-items-center">
          <div className="cart position-relative" onClick={() => setShowCart(!showCart)}>
            <span>ðŸ›’</span>
            <span className="badge bg-danger">{cart.length}</span>
          </div>
          <Col className="ml-3">
            <Button variant="danger" onClick={clearCart}>
              Limpar Carrinho
            </Button>
          </Col>
        </div>
      </header>

      {showCart && (
        <div className="cart-items position-fixed top-0 end-0 m-3 bg-white border rounded shadow-lg p-3">
          <h2>Itens no Carrinho</h2>
          <ul>
            {cart.map((item, index) => (
              <li key={index}>{item.name} - R${item.price.toFixed(2)}</li>
            ))}
          </ul>
        </div>
      )}

      <Row>
        {products.map((product, index) => (
          <Col key={index} md={4} className="mb-4">
            <Card>
              <Card.Img variant="top" src={product.image} />
              <Card.Body>
                <Card.Title>{product.name}</Card.Title>
                {product.listPrice > product.price && (
                  <Card.Text>
                    <span className="text-muted text-decoration-line-through">De: R${product.listPrice.toFixed(2)}</span><br />
                    <span className="text-primary">Por: R${product.price.toFixed(2)}</span>
                  </Card.Text>
                )}
                <Button variant="success" onClick={() => addToCart(product)}>
                  Adicionar ao Carrinho
                </Button>
              </Card.Body>
            </Card>
          </Col>
        ))}
      </Row>
    </Container>
  );
}

export default App;
