import React, { useState, useEffect } from 'react';
import ProductCard from './components/ProductCard';

const App = () => {
  const [products, setProducts] = useState([]);

  useEffect(() => {
    const data = {
      "products": [
        {
          "sku": "1",
          "name": "Máscara Descartável Granmask Tripla Camada Branca com Elástico 50 Unidades",
          "image": "https://araujo.vteximg.com.br/arquivos/ids/4133028-200-200/07893595226862.jpg",
          "priceSpecification": {
            "price": 45.0,
            "oldPrice": 50.0,
            "discount": 10
          }
        },
        {
          "sku": "2",
          "name": "Actine Darrow Sabonete Líquido 400ml",
          "image": "https://www.araujo.com.br/arquivos/ids/4141233-200-200/03282770201826_0.jpg",
          "priceSpecification": {
            "price": 55.99,
            "oldPrice": 60.99
          }
        },
        {
          "sku": "3",
          "name": "Soro Fisiológico 0,9% Mió com 500ml",
          "image": "https://www.araujo.com.br/arquivos/ids/4063633-200-200/07896830001736.jpg",
          "priceSpecification": {
            "price": 6.49
          }
        },
        {
          "sku": "4",
          "name": "Fralda Cremer Disney Baby Tamanho G Prática com 24 Fraldas Descartáveis",
          "image": "https://www.araujo.com.br/arquivos/ids/4077905-200-200/07898064035587.jpg",
          "priceSpecification": {
            "price": 13.90,
            "oldPrice": 23.49,
            "discount": 40
          }
        },
        {
          "sku": "5",
          "name": "Rosuvastatina 10mg EMS Genérico com 30 Comprimidos",
          "image": "https://www.araujo.com.br/arquivos/ids/4056257-200-200/07896004729640.jpg",
          "priceSpecification": {
            "price": 15.99,
            "oldPrice": 106.72,
            "discount": 85
          }
        },
        {
          "sku": "6",
          "name": "L'Oréal Professionnel Expert Absolut Repair Cortex Lipidium - Máscara de Reconstrução 500g",
          "image": "https://www.araujo.com.br/arquivos/ids/4137619-200-200/07896523200576.jpg",
          "priceSpecification": {
            "price": 22.56,
            "oldPrice": 11.79,
            "discount": 47
          }
        }
      ]
    };
    setProducts(data.products);
  }, []);

  return (
    <div className="App">
      <h1>Vitrine de Produtos</h1>
      <div className="product-grid">
        {products.map((product, index) => (
          <ProductCard key={index} {...product} />
        ))}
      </div>
    </div>
  );
}

export default App;
