import React from 'react';

const ProductCard = ({ product, addToCart }) => {
  const { name, listPrice, price, image } = product;
  const hasDiscount = listPrice > price;

  return (
    <div className="card h-100">
      <img src={image} className="card-img-top" alt={name} />
      <div className="card-body">
        <h5 className="card-title">{name}</h5>
        {hasDiscount && <span className="badge bg-danger">Desconto</span>}
        <p className="card-text">
          <span className="text-muted text-decoration-line-through">De: R${listPrice.toFixed(2)}</span>
          <br />
          <span className="text-primary">Por: R${price.toFixed(2)}</span>
        </p>
        <button className="btn btn-success" onClick={() => addToCart(product)}>
          Adicionar ao Carrinho
        </button>
      </div>
    </div>
  );
};

export default ProductCard;
