import React, { useState, useEffect } from 'react';
import { Container, Row, Col, Button, Card } from 'react-bootstrap';
import ProductCard from './components/ProductCard';
import 'bootstrap/dist/css/bootstrap.min.css';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

const App = () => {
  const [products, setProducts] = useState([]);
  const [cart, setCart] = useState([]);
  const [showCart, setShowCart] = useState(false);

  const cartRef = useRef(null);

  useEffect(() => {
    const data = {
      "products": [
        {
          "id": 1,
          "name": "CalÃ§a Alfaiataria Rendada",
          "listPrice": 243.90,
          "price": 225.90,
          "image": "https://lojalancaperfume.vtexassets.com/arquivos/ids/504997-150-200/"
        },
        {
          "id": 2,
          "name": "CalÃ§a Alfaiataria Rendada",
          "listPrice": 243.90,
          "price": 225.90,
          "image": "https://lojalancaperfume.vtexassets.com/arquivos/ids/504997-150-200/"
        },
        {
          "id": 3,
          "name": "CalÃ§a Alfaiataria Rendada",
          "listPrice": 243.90,
          "price": 225.90,
          "image": "https://lojalancaperfume.vtexassets.com/arquivos/ids/504997-150-200/"
        }
      ]
    };
    setProducts(data.products);
  }, []);

  const addToCart = (product) => {

    if (cart.includes(product)) {
      toast.warn(`${product.name} ja foi adicionado ao carrinho!`);
      return;
    }
    setCart([...cart, product]);
    toast.success(`${product.name} adicionado ao carrinho!`);
  };

  const clearCart = () => {
    setCart([]);
    toast.info('Carrinho limpo!');
  };

  return (
    <Container>
      <header className="d-flex justify-content-between align-items-center py-3 position-relative">
        <h1>Vitrine de Produtos</h1>
        <div className="d-flex align-items-center">
          <div className="cart position-relative" onClick={() => setShowCart(!showCart)}>
            <span>ðŸ›’</span>
            <span className="badge bg-danger">{cart.length}</span>
          </div>
        </div>
      </header>

      {showCart && (
        <div className="cart-items position-fixed top-0 end-0 m-3 bg-white border rounded shadow-lg p-3">
          <h2>Itens no Carrinho</h2>
          <ul>
            {cart.map((item, index) => (
              <li key={index}>
                <Row>
                  <Col sm={3}>
                    <Card.Img variant="top" src={item.image} />
                  </Col>
                  <Col sm={4}>
                    {item.name}
                  </Col>
                  <Col sm={3} style={{ fontWeight: 'bold' , textAlign: 'right'}}>
                    R${item.listPrice.toFixed(2)}
                  </Col>
                </Row>
              </li>
            ))}
          </ul>
          <Col className="ml-3">
            <Button variant="danger" onClick={clearCart}>
              Limpar Carrinho
            </Button>
          </Col>
        </div>
      )}

      <Row>
        {products.map((product, index) => (
          <ProductCard key={index} product={product} addToCart={addToCart} />
        ))}
      </Row>

      <ToastContainer
        position="bottom-right"
        autoClose={5000}
        hideProgressBar
        newestOnTop
        closeOnClick
        rtl={false}
        pauseOnFocusLoss
        draggable
        pauseOnHover
      />
    </Container>
  );
}

export default App;
